/**
 * Handles Gdn_Form's color input.
 * The input with the js-color-picker-value class stores the last valid hex value (the color shown in the preview span).
 *
 * This is the expected construction for the color picker (can be built using Gdn_Form's color() method):
 *
 * <div class="js-color-picker color-picker input-group" data-allow-empty="false">
 *     <input type="text" class="js-color-picker-value color-picker-value InputBox Hidden">
 *     <input type="text" class="js-color-picker-text color-picker-text InputBox">
 *     <span class="js-color-picker-preview color-picker-preview"></span>
 *     <input type="color" class="js-color-picker-color color-picker-color">
 * </div>
 *
 * You can set whether or not to allow empty values usings the data-allow-empty attribute.
 */

/**
 * @type {{start: Function, isValid: Function, isHex: Function, normalizeHex: Function}}
 */
var colorPicker = {

    /**
     * Starts the color picker javascript
     */
    start: function($input) {
        var allowEmpty = false;

        if ($input.data('allowEmpty') === true) {
            allowEmpty = true;
        }

        if ($input.find('.js-color-picker-value').val()) {
            var color = $input.find('.js-color-picker-value').val();
            $input.find('.js-color-picker-text').val(color);
            $input.find('.js-color-picker-preview').css('background-color', color);
        } else {
            // Empty value
            $input.find('.js-color-picker-text').val('');
            $input.find('.js-color-picker-preview').css('background-color', 'transparent');
        }

        // Selecting based on picker
        $input.find('.js-color-picker-color').change(function () {
            var color = $(this).val();
            $input.find('.js-color-picker-text').val(color);
            $input.find('.js-color-picker-preview').css('background-color', color);
            $input.find('.js-color-picker-value').val(color);
        });

        // Selecting based on text
        $input.find('.js-color-picker-text').on('input', function () {
            var candidateColor = $(this).val();
            var color;

            if (colorPicker.isHex(candidateColor)) {
                color = colorPicker.normalizeHex($(this).val());
                $input.find('.js-color-picker-color').val(color); // set color picker value
            } else if (colorPicker.isValidNonColor(candidateColor)) {
                color = $(this).val()
            } else if (allowEmpty && candidateColor === '') {
                // The text field is empty. Set preview to tranparent and value to empty.
                $input.find('.js-color-picker-preview').css('background-color', 'transparent');
                $input.find('.js-color-picker-value').val('');
            }

            if (color !== undefined) {
                $input.find('.js-color-picker-preview').css('background-color', color); // set preview color
                $input.find('.js-color-picker-value').val(color); // set value
            }
        });

        // Trigger input color picker element on clicking preview area
        $input.find('.js-color-picker-preview').on('click', function () {
            $input.find('.js-color-picker-color').trigger('click');
            $input.find('.js-color-picker-color').val($input.find('.js-color-picker-value').val());
        });
    },

    /**
     * Tests whether the color value is in the valid values list below.
     *
     * @param string The string to test.
     * @returns {boolean} Whether the value is in the valid values list.
     */
    isValidNonColor: function(string) {
        var validValues = ['transparent', 'inherit', 'initial'];
        if (validValues.indexOf(string.toLowerCase()) > -1) {
            return true;
        }
        return false;
    },

    /**
     * Tests whether we have a valid six or three-character hex code, with or without the opening hash.
     *
     * @param string The string to test.
     * @returns {boolean} Whether the hex code is valid.
     */
    isHex: function(string) {
        var regex = new RegExp(/(^#?[0-9A-F]{6}$)|(^#?[0-9A-F]{3}$)/i);
        if (regex.test(string)) {
            return true;
        }
        return false;
    },

    /**
     * Ensures a valid hex code begins with a hash and if expanded to its six-character equivalent.
     * Many system color pickers need the six-character hash value.
     *
     * @param color A valid six or three-character hex code, with or without the leading hash.
     * @returns {string} A six-character hex code with the leading hash.
     */
    normalizeHex: function(color) {
        if (!colorPicker.isHex(color)) {
            return '';
        }
        if (color.substr(0, 1) === '#') {
            color = color.substr(1);
        }
        if (color.length === 3) {
            color = color.substr(0, 1) + color.substr(0, 1)
            + color.substr(1, 1) + color.substr(1, 1)
            + color.substr(2, 1) + color.substr(2, 1);
        }
        color = '#' + color;
        return color;
    }
}

jQuery(document).ready(function($) {
    $('.js-color-picker').each(function() {
        colorPicker.start($(this));
    });
});

var crop = {

    element: null,

    start: function(element) {
        crop.element = element;
        $('#save-crop', crop.element).hide();
        if ($.Jcrop) {
            // Set jcrop settings on source image.
            $('#cropbox', crop.element).Jcrop({
                onChange: crop.setPreviewAndCoords,
                onSelect: crop.setPreviewAndCoords,
                onRelease: crop.removePreviewAndCoords,
                aspectRatio: 1
            });
        }
    },

    /**
     * Changes the preview cropped image based on the crop parameters and updates the form values.
     *
     * @param c The source image crop.element to crop.
     */
    setPreviewAndCoords: function (c) {
        $(document).trigger('cropStart');

        // Show the preview image and the 'save' button and hide the current cropped image.
        $('#preview', crop.element).show();
        $('#current-crop', crop.element).hide();
        $('#save-crop', crop.element).show();

        // Get constants from the form's hidden inputs.
        var cropSizeWidth = $('#Form_CropSizeWidth', crop.element).val();
        var cropSizeHeight = $('#Form_CropSizeHeight', crop.element).val();
        var sourceHeight = $('#Form_HeightSource', crop.element).val();
        var sourceWidth = $('#Form_WidthSource', crop.element).val();

        // Set the ratios for scaling.
        var rx = cropSizeWidth / c.w;
        var ry = cropSizeHeight / c.h;

        // Set the form's hidden inputs based on the crop's position and size.
        $('#Form_x', crop.element).val(c.x);
        $('#Form_y', crop.element).val(c.y);
        $('#Form_w', crop.element).val(c.w);
        $('#Form_h', crop.element).val(c.h);

        // Calculate the preview image's css rules.
        $('#preview').css({
            width: Math.round(rx * sourceWidth) + 'px',
            height: Math.round(ry * sourceHeight) + 'px',
            marginLeft: '-' + Math.round(rx * c.x) + 'px',
            marginTop: '-' + Math.round(ry * c.y) + 'px'
        });
    },

    /**
     * Hides the cropping preview and save button and shows the current cropped image.
     */
    removePreviewAndCoords: function() {
        $(document).trigger('cropEnd');
        if ($('#current-crop', crop.element)) {
            $('#current-crop', crop.element).show();
            $('#preview', crop.element).hide();
            $('#save-crop', crop.element).hide();
        }
    }

}


$(document).on('contentLoad', function(e) {
    if ($('#cropbox', e.target).length === 0) {
        return;
    }
    crop.start(e.target);
});

/**
 * If a btn-group gets too long for the window width, this will transform it into a dropdown-filter.
 *
 * Selector: `.btn-group`
 *
 * @param element - The scope of the function.
 */
var buttonGroup = function(element) {

    /**
     * Transforms a button group into a dropdown-filter.
     *
     * @param $buttonGroup
     */
    var transformButtonGroup = function(buttonGroup) {
        var elem = document.createElement('div');
        $(elem).addClass('dropdown');
        $(elem).addClass('dropdown-filter');

        var items = $(buttonGroup).html();
        var title = gdn.definition('Filter');
        var list = document.createElement('div');
        var id = Math.random().toString(36).substr(2, 9);


        $(list).addClass('dropdown-menu');
        $(list).attr('aria-labelledby', id);
        $(list).html(items);

        $('.btn', list).each(function() {
            $(this).removeClass('btn');
            $(this).removeClass('btn-secondary');
            $(this).addClass('dropdown-item');

            if ($(this).hasClass('active')) {
                title = $(this).html();
            }
        });

        $(elem).prepend(
            '<button ' +
            'id="' + id + '" ' +
            'type="button" ' +
            'class="btn btn-secondary dropdown-toggle" ' +
            'data-toggle="dropdown" ' +
            'aria-haspopup="true" ' +
            'aria-expanded="false"' +
            '>' +
            title +
            '</button>'
        );

        $(elem).append($(list));

        return elem;
    };

    var showButtonGroup = function(buttonGroup, dropdown) {
        $(buttonGroup).show();
        $(dropdown).hide();
    };

    var showDropdown = function(buttonGroup, dropdown) {
        $(buttonGroup).hide();
        $(dropdown).show();
    };

    /**
     * Generates an equivalent dropdown to the btn-group. Calculates widths to see whether we show the dropdown
     * or btn-group, and then shows/hides the appropriate one.
     *
     * @param element The scope of the function.
     */
    var checkWidth = function(element) {
        $('.btn-group', element).each(function() {
            var self = this;
            var maxWidth = $(self).data('maxWidth');
            var container = $(self).data('containerSelector');

            if (!container && !maxWidth) {
                maxWidth = $(window).width();
            }

            if (container) {
                maxWidth = $(container).width();
            }

            if (!self.width) {
                self.width = $(self).width();
            }

            if (!self.dropdown) {
                self.dropdown = transformButtonGroup(self);
                $(self).after(self.dropdown);
            }

            if (self.width <= maxWidth) {
                showButtonGroup(self, self.dropdown);
            } else {
                showDropdown(self, self.dropdown);
            }
        });
    };

    checkWidth(element);

    $(window).resize(function() {
        checkWidth(document);
    });
};

